# add dummy_id field now
exercises = Exercise.all
current_lesson = 1
current_exercise = 1

exercises.each do |exercise|
	if exercise.lesson_id != current_lesson
		current_lesson += 1
		current_exercise = 1
	end

	exercise.update(dummy_id: current_exercise)
	current_exercise += 1
end